import { auth0 } from 'auth0/auth0-js';

const webAuth = new auth0.webAuth({
    domain: "dev-oq0noun8v2cmmgv6.us.auth0.com",
    clientId: "cZBNIMJP9oSJVGAHlb9GJ2Pm00yE9cdJ",
    redirectUri: "",
    responseType: "token id_token",
    scope: "openid profile",
});

const login = () => {
    webAuth.authorize();
};

export { login };
