import { createApp } from 'vue';
import { mount } from 'my_vue_app/HelloWorld'; // The URL of the remote's entry file

createApp(mount()).mount('#app');

<template>
  <div>
    <h1>Customer List</h1>
    <ul>
      <li v-for="customer in customers" :key="customer.id">
        {{ customer.name }}
      </li>
    </ul>
  </div>
</template>

<script>
export default {
  data() {
    return {
      customers: [],
    };
  },
  async created() {
    const customers = await this.$customer.getCustomers();
    this.customers = customers;
  },
};
</script>

<style scoped>
.hello {
  font-size: 2em;
}
